BEGIN; 

-- Delete tables if they exists
DROP TABLE IF EXISTS 
    "session",
    "best_performance",
    "user_has_sport",
    "sport",
    "category",
    "user";

-- Create user table
CREATE TABLE "user" (
    "id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "email" text NOT NULL UNIQUE,
    "password" text NOT NULL,
    "firstname" text NOT NULL,
    "lastname" text NOT NULL,
    "birth_date" date,
    "city" text,
    "country" varchar(50),
    "gender" text NOT NULL,
    "weight" numeric,
    "height" numeric,
    "is_shared" boolean,
    "profile_picture" text,
    "created_at" timestamptz NOT NULL DEFAULT now(),
    "updated_at" timestamptz
);

-- Création de la table 'category'
CREATE TABLE "category" (
    "id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "name" text NOT NULL UNIQUE,
    "created_at" timestamptz NOT NULL DEFAULT now(),
    "updated_at" timestamptz
);

-- Création de la table 'sport'
CREATE TABLE "sport" (
    "id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "name" text NOT NULL UNIQUE,
    "unit" text NOT NULL,
    "category_id" integer NOT NULL REFERENCES "category"("id") ON DELETE CASCADE,
    "created_at" timestamptz NOT NULL DEFAULT now(),
    "updated_at" timestamptz
);

-- Création de la table 'user_has_sport'
CREATE TABLE "user_has_sport" (
    "id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "user_id" integer NOT NULL REFERENCES "user"("id") ON DELETE CASCADE,
    "sport_id" integer NOT NULL REFERENCES "sport"("id") ON DELETE CASCADE,
    "created_at" timestamptz NOT NULL DEFAULT now(),
    "updated_at" timestamptz,
    UNIQUE ("user_id", "sport_id")
);

-- Création de la table 'best_performance'
CREATE TABLE "best_performance" (
    "id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "best_score" numeric NOT NULL,
    "date" date NOT NULL,
    "user_id" integer NOT NULL REFERENCES "user"("id") ON DELETE CASCADE,
    "sport_id" integer NOT NULL REFERENCES "sport"("id") ON DELETE CASCADE,
    "created_at" timestamptz NOT NULL DEFAULT now(),
    "updated_at" timestamptz
);

-- Création de la table 'session'
CREATE TABLE "session" (
    "id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "date" date NOT NULL,
    "description" VARCHAR(500),
    "user_id" integer NOT NULL REFERENCES "user"("id") ON DELETE CASCADE,
    "sport_id" integer NOT NULL REFERENCES "sport"("id") ON DELETE CASCADE,
    "created_at" timestamptz NOT NULL DEFAULT now(),
    "updated_at" timestamptz
);

COMMIT;